mapper.select:
SELECT o_sNumber , o_phone , o_cellPhone , o_name , o_address , o_latitude , o_longitude , o_time1 , o_time2 , o_image , (6371 * acos(cos(radians(#{o_latitude})) * cos(radians(o_latitude)) * cos(radians(o_longitude) - radians(#{o_longitude})) + sin(radians(#{o_latitude})) * sin(radians(o_latitude)))) AS distance FROM owner
where o_approval = 1
HAVING distance <= #{radius}
ORDER BY distance
LIMIT 0,300;
