mapper.select:
select count(*)
from owner
where o_sNumber = #{o_sNumber}

select *
from owner
where o_sNumber = #{id}

select *
from goods
where g_owner = #{o_sNumber}

mapper.update:
update reserve
set r_status = 1
where r_code = #{r_code}

update goods
set g_count = g_count + #{r_count}
where g_code = #{r_g_code}

update reserve
set r_status = 2
where r_code = #{r_code}

update goods
set g_status = if(g_count != 0 and g_status != 2, 0, g_status)
where g_code = #{r_g_code}

update user
set u_point = u_point + #{r_pay} * 0.1
where u_id = #{r_u_id}

update reserve
set r_status = 4
where r_code = #{r_code}

update user
set u_noShowCnt = u_noShowCnt + 1
where u_id = #{r_u_id}

update user
set u_status = if(u_noShowCnt >= 5, 2, u_status)
where u_id = #{r_u_id}

update goods
set g_status = 2
where g_code = #{g_code}

mapper.select:
select *
from reserve
where r_code = #{r_code};

mapper.update:
update goods
set g_status = 2
where g_code = #{g_code}

mapper.select:
select *
from reserve
where r_owner = #{g_owner}

select *
from goods
where g_code = #{r_g_code}

select *
from goods
where g_category LIKE '%${g_category}%'
  AND g_name LIKE '%${searchInput}%'
  AND g_status = #{g_status}
  AND g_owner = #{g_owner}

select count(*)
from goods
where g_owner = #{o_sNumber}

mapper.update:
update reserve
set r_status = 2
where r_g_code = #{g_code}

mapper.select:
select a.r as date, ifnull(b.cnt, 0) as sum
from (with recursive c as (select 1 as r union all select r + 1 from c where r < 12) select r from c) as a
         left join (select month(r_lastTime) as mon, sum(r_pay) as cnt
                    from reserve
                    where r_status = 3
                      and r_owner = #{o_name}
                      and year(r_lastTime) = #{total}
                    group by mon) as b
                   on a.r = b.mon;

select year(r_lastTime) as date, ifnull(sum(r_pay), 0) as sum
from reserve
where r_owner = #{o_sNumber}
  and r_status = 3
group by date
order by date;

select count(*)
from firebase_owner_push_token
where token = #{token}
