mapper.select:
select count(*)
from owner
where o_sNumber = #{o_sNumber}

mapper.update:
update goods
set g_count = g_count - #{r_count}
where g_code = #{r_code}

update reserve
set r_status = 1
where r_code = #{r_code}

update goods
set g_count = g_count + #{r_count}
where g_code = #{r_g_code}

update reserve
set r_status = 2
where r_code = #{r_code}

update goods
set g_status = if(g_count != 0 and g_status != 2, 0, g_status)
where g_code = #{r_g_code}

update reserve
set r_status = 4
where r_code = #{r_code}

update user
set u_noShowCnt = u_noShowCnt + 1
where u_id = #{r_u_id};

update goods
set g_status = if(g_count != 0 and g_status != 2, 0, g_status)
where g_code = #{r_g_code}

update goods
set g_status = 2
where g_code = #{g_code}
