mapper.select:
select count(*)
from user
where u_id = #{u_id}

select *
from user
where u_id = #{u_id}
  and u_status = 0;

select count(*)
from favorites
where f_o_sNumber = #{f_o_sNumber}
  and f_p_user_id = #{f_p_user_id}

mapper.delete:
delete
from favorites
where f_o_sNumber = #{f_o_sNumber}
  and f_p_user_id = #{f_p_user_id}

mapper.select:
select *
from reserve
where r_code = #{r_code}
  and r_u_id = #{r_u_id};

mapper.update:
update reserve
set r_status = 5
where r_code = #{r_code}

mapper.select:
select o.o_name, o.o_sNumber as f_o_sNumber, o.o_approval, o.o_phone, o.o_address, o.o_time1, o.o_time2
from owner as o,
     favorites as f
where f.f_p_user_id = #{u_id}
  and o.o_sNumber = f.f_o_sNumber
group by f.f_o_sNumber;

mapper.update:
update goods
set g_count = g_count - #{r_count}
where g_code = #{r_g_code}

update goods
set g_status = if(g_count = 0 AND g_status != 2, 1, g_status)
where g_code = #{r_g_code}

mapper.select:
select u_noShowCnt
from user
where u_id = #{u_id}

mapper.update:
update user
set u_status = 1
where  u_id = #{u_id}
